CREATE PROCEDURE dbo.Sale_IU @InvoiceNo INT,
@Reference VARCHAR(20),
@InvoiceDate DATE,
@CustomerCode VARCHAR(13),
@Total INT,
@SaleCode VARCHAR(13),
@Remarks VARCHAR(200),
@Returned BIT,
@CreatedBy VARCHAR(50),
@UpdatedBy VARCHAR(50),
@ReturnValue INT OUTPUT

AS
BEGIN
  SET NOCOUNT ON
  DECLARE @myNo INT;
  DECLARE @Balance INT;
  DECLARE @AccountCode VARCHAR(13);


  BEGIN TRY

    IF EXISTS (SELECT
          1
        FROM dbo.SaleHead sh
        WHERE sh.InvoiceNo = @InvoiceNo)

    BEGIN
      --Getting Previous Balance

      SELECT
        @AccountCode = CustomerCode
       ,@Balance = Total
      FROM dbo.SaleHead
      WHERE InvoiceNo = @InvoiceNo

      --Updating Balance
      UPDATE AccountCodes
      SET AccountCodes.Balance = ISNULL(AccountCodes.Balance, 0) - @Balance
      WHERE AccountCodes.AccountCode = @CustomerCode


      UPDATE dbo.SaleHead
      SET Reference = @Reference
         ,InvoiceDate = @InvoiceDate
         ,CustomerCode = @CustomerCode
         ,Total = @Total
         ,SaleCode = @SaleCode
         ,Returned = @Returned
         ,UpdatedBy = @UpdatedBy
         ,UpdationDate = GETDATE()
         ,Remarks = @Remarks
      WHERE InvoiceNo = @InvoiceNo

      --Updating New Balance
      UPDATE AccountCodes
      SET AccountCodes.Balance = ISNULL(AccountCodes.Balance, 0) + @Total
      WHERE AccountCodes.AccountCode = @CustomerCode

      SET @ReturnValue = @InvoiceNo
    END

    ELSE

    BEGIN
      SET @myNo = (SELECT
          ISNULL(MAX(InvoiceNo), 0) + 1
        FROM dbo.SaleHead)

      INSERT INTO dbo.SaleHead (InvoiceNo, Reference, InvoiceDate, CustomerCode, Total, SaleCode, Returned, CreatedBy, CreationDate, Remarks)
        VALUES (@myNo, @Reference, @InvoiceDate, @CustomerCode, @Total, @SaleCode, @Returned, @CreatedBy, GETDATE(), @Remarks)

      --Updating New Balance
      UPDATE AccountCodes
      SET AccountCodes.Balance = ISNULL(AccountCodes.Balance, 0) + @Total
      WHERE AccountCodes.AccountCode = @CustomerCode


      SET @ReturnValue = @myNo

    END

    IF @@rowcount = 0
    BEGIN
      RETURN 0
    END

    ELSE
    BEGIN
      RETURN @ReturnValue
    END

  END TRY

  BEGIN CATCH
    DECLARE @Error_Message VARCHAR(150)
    SET @Error_Message = ERROR_NUMBER() + ' ' + ERROR_MESSAGE()
    RAISERROR (@Error_Message, 16, 1)

    RETURN -1

  END CATCH

END
