CREATE PROCEDURE dbo.DeleteJvSub @VNo AS INT

AS

BEGIN

  SET NOCOUNT ON
  --Variable to Hold Last Closing Balance
  DECLARE @LastClosing INT;

  --Variables to Hold Current Amount
  DECLARE @AccountCode VARCHAR(13);
  DECLARE @Amount INT;
  DECLARE @RowID AS INT;

  --Variables Hoding New Balance
  DECLARE @NewClosing INT;
  --

  DECLARE @JvSub AS CURSOR;

  SET @JvSub = CURSOR FAST_FORWARD FOR SELECT
    jv.AccountCode
   ,jv.Credit - jv.Debit AS Amount
   ,jv.RowID
  FROM JvSub jv
  WHERE jv.VNo = @VNo
  ORDER BY jv.RowID;

  OPEN @JvSub;
  FETCH NEXT FROM @JvSub INTO @AccountCode, @Amount, @RowID;

  WHILE @@FETCH_STATUS = 0
  BEGIN

  -- Getting Closing Balance
  SELECT
    @LastClosing = ISNULL(ac.Balance, 0)
  FROM AccountCodes ac
  WHERE ac.AccountCode = @AccountCode

  -- Setting Current Closing Balance

  SET @NewClosing = @LastClosing + @Amount

  --

  UPDATE AccountCodes
