CREATE PROCEDURE dbo.usp_TB4 @AsOnDate AS DATETIME

AS
  SET NOCOUNT ON
  BEGIN

    SELECT
      AccountCodes.AccountCode
     ,AccountCodes.Description
     ,IIF(SUM(vw_Ledger.Debit) - SUM(vw_Ledger.Credit) > 0, SUM(vw_Ledger.Debit) - SUM(vw_Ledger.Credit), 0) AS Debit
     ,IIF(SUM(vw_Ledger.Debit) - SUM(vw_Ledger.Credit) < 0, -(SUM(vw_Ledger.Debit) - SUM(vw_Ledger.Credit)), 0) AS Credit

    FROM dbo.vw_Ledger
    INNER JOIN dbo.AccountCodes
      ON vw_Ledger.AccountCode = AccountCodes.AccountCode
    WHERE vw_Ledger.VDate <= @AsOnDate
    GROUP BY AccountCodes.AccountCode
            ,AccountCodes.Description
  END
