CREATE PROCEDURE dbo.UpdateBalance

AS

BEGIN

  SET NOCOUNT ON

  --Variables to Hold Current Amount
  DECLARE @AccountCode VARCHAR(13);
  DECLARE @Closing INT;


  DECLARE @Accounts AS CURSOR;

  SET @Accounts = CURSOR FAST_FORWARD FOR SELECT
    ac.AccountCode
   ,ac.Balance
  FROM AccountCodes ac
  ORDER BY ac.AccountCode;

  OPEN @Accounts;
  FETCH NEXT FROM @Accounts INTO @AccountCode, @Closing;

  WHILE @@FETCH_STATUS = 0
  BEGIN

  -- Getting Closing Balance
  SELECT
    @Closing = SUM(Debit) - SUM(Credit)
  FROM dbo.vw_Ledger vl
  WHERE (vl.AccountCode = @AccountCode)
  GROUP BY vl.AccountCode


  UPDATE AccountCodes
  SET AccountCodes.Balance = @Closing --ISNULL(Products.StockInHand, 0) - @Quantity
  WHERE AccountCodes.AccountCode = @AccountCode


  FETCH NEXT FROM @Accounts INTO @AccountCode, @Closing;
  END

  CLOSE @Accounts;
  DEALLOCATE @Accounts;


END
GO

