CREATE PROCEDURE dbo.PurchaseDetail_Insert @InvoiceNo INT,
@ProductCode INT,
@ProductRate DECIMAL(8, 2),
@Quantity INT,
@Discount DECIMAL(4, 2)
AS
BEGIN

  SET NOCOUNT ON;



  DECLARE @TotalQty INT;

  DECLARE @LastPrice AS DECIMAL(8, 2);
  DECLARE @LastClosing AS INT;
  DECLARE @LastAmount AS DECIMAL(10, 2);
  


  SELECT @LastPrice = ISNULL(p.CalcPrice, 0)
   ,@LastClosing = ISNULL(p.StockInHand, 0)

  FROM dbo.Products p
  WHERE p.ProductCode = @ProductCode;

  SET @TotalQty = @Quantity

  SET @LastAmount = @LastPrice * @LastClosing;

  DECLARE @Amount DECIMAL(10, 2);
  DECLARE @Dis DECIMAL(10, 2);
  DECLARE @Total DECIMAL(10, 2);
  DECLARE @CalcPrice DECIMAL(8, 2);

  SET @Amount = @ProductRate * @Quantity;
  SET @Dis = @Amount * @Discount;
  SET @Total = @Amount - @Dis;

  DECLARE @NewAmount DECIMAL(10, 2);
  DECLARE @NewClosing INT;

  SET @NewAmount = @LastAmount + @Total;
  SET @NewClosing = @LastClosing + @TotalQty;

  IF @NewClosing = 0
  BEGIN
    SET @CalcPrice = @LastPrice;
  END;

  ELSE
  BEGIN
    SET @CalcPrice = @NewAmount / @NewClosing;
  END;

  INSERT INTO dbo.PurchaseDetail (InvoiceNo, ProductCode, ProductRate, Quantity, Discount)
  VALUES (@InvoiceNo, @ProductCode, @ProductRate, @Quantity, @Discount);

  DECLARE @LastRate DECIMAL(8,2)
  
SELECT @LastRate = ISNULL(gp.ProductRate,0) FROM dbo.udtfGetPRate(@ProductCode) gp 

  UPDATE dbo.Products
  SET Products.StockInHand = @NewClosing
     ,Products.CalcPrice = @CalcPrice
     ,LastPrice = @LastRate
  WHERE Products.ProductCode = @ProductCode;

END;
GO

