CREATE PROCEDURE dbo.DeletePurchaseDetail @InvoiceNo AS INT

AS

BEGIN

  SET NOCOUNT ON
  --Variable to Hold Last Price & Closing Balance
  DECLARE @LastPrice DECIMAL(8, 2);
  DECLARE @LastClosing INT;
  DECLARE @LastAmount DECIMAL(10, 2);
  DECLARE @LastRate DECIMAL(8, 2)

  --Variables to Hold Current Price & Quantity
  DECLARE @ProductCode INT;
  DECLARE @ProductRate DECIMAL(8, 2);
  DECLARE @Quantity INT;
  DECLARE @Discount DECIMAL(4, 2);
  DECLARE @RowID INT;


  --Variables Hoding New Amount & Quantity
  DECLARE @Amount DECIMAL(10, 2)
  DECLARE @Dis DECIMAL(10, 2)
  DECLARE @Total DECIMAL(10, 2)
  DECLARE @CalcPrice DECIMAL(8, 2)

  DECLARE @NewAmount DECIMAL(10, 2)
  DECLARE @NewClosing INT

  --
  DECLARE @TotalQty INT;

  DECLARE @PurchaseDetail AS CURSOR;


  SET @PurchaseDetail = CURSOR FAST_FORWARD FOR SELECT
    ProductCode
   ,ProductRate
   ,Quantity
   ,Discount
   ,RowID
  FROM PurchaseDetail
  WHERE InvoiceNo = @InvoiceNo
  ORDER BY RowID;

  OPEN @PurchaseDetail;
  FETCH NEXT FROM @PurchaseDetail INTO @ProductCode, @ProductRate, @Quantity, @Discount, @RowID;

  WHILE @@FETCH_STATUS = 0
  BEGIN

  -- Getting Last Price & Closing Balance
  SELECT
    @LastPrice = ISNULL(p.CalcPrice, 0)
   ,@LastClosing = ISNULL(P.StockInHand, 0)
  FROM Products p
  WHERE p.ProductCode = @ProductCode

  SET @LastAmount = @LastPrice * @LastClosing

  -- Setting Current Price & Closing Balance

  SET @Amount = @ProductRate * @Quantity
  SET @Dis = @Amount * @Discount
  SET @Total = @Amount - @Dis

  SET @TotalQty = @Quantity

  SET @NewAmount = @LastAmount - @Total
  SET @NewClosing = @LastClosing - @TotalQty


  IF @NewClosing = 0
  BEGIN
    SET @CalcPrice = @LastPrice
  END
  ELSE
  BEGIN
    SET @CalcPrice = @NewAmount / @NewClosing
  END
  --

    DELETE FROM PurchaseDetail
    WHERE RowID = @RowID


  SELECT
    @LastRate = ISNULL(gp.ProductRate, 0)
  FROM dbo.udtfGetPRate(@ProductCode) gp

  UPDATE dbo.Products
  SET Products.StockInHand = @NewClosing
     ,Products.CalcPrice = @CalcPrice
     ,LastPrice = @LastRate
  WHERE Products.ProductCode = @ProductCode;


  FETCH NEXT FROM @PurchaseDetail INTO @ProductCode, @ProductRate, @Quantity, @Discount, @RowID;
  END

  CLOSE @PurchaseDetail;
  DEALLOCATE @PurchaseDetail;


END
GO

