CREATE PROCEDURE dbo.UpdateStock

AS

BEGIN

  SET NOCOUNT ON

  --Variables to Hold Current Amount
  DECLARE @ProductCode INT;
  DECLARE @Closing INT;


  DECLARE @Products AS CURSOR;

  SET @Products = CURSOR FAST_FORWARD FOR SELECT
    p.ProductCode
   ,p.StockInHand
  FROM Products p
  ORDER BY p.ProductCode;

  OPEN @Products;
  FETCH NEXT FROM @Products INTO @ProductCode, @Closing;

  WHILE @@FETCH_STATUS = 0
  BEGIN

  -- Getting Closing Balance
  SELECT
    @Closing = SUM(vw_myStock.Quantity) 
  FROM dbo.vw_myStock
  WHERE (vw_myStock.ProductCode = @ProductCode)
  GROUP BY vw_myStock.ProductCode


  UPDATE dbo.Products
  SET dbo.Products.StockInHand = @Closing --ISNULL(Products.StockInHand, 0) - @Quantity
  WHERE (dbo.Products.ProductCode = @ProductCode)


  FETCH NEXT FROM @Products INTO @ProductCode, @Closing;
  END

  CLOSE @Products;
  DEALLOCATE @Products;


END
GO

