CREATE PROCEDURE dbo.BackupDB @backupDirectory VARCHAR(1000)
AS
  DECLARE @backupFileName VARCHAR(100)
         ,@databaseDataFilename VARCHAR(100)
         ,@databaseLogFilename VARCHAR(100)
         ,@databaseDataFile VARCHAR(100)
         ,@databaseLogFile VARCHAR(100)
         ,@execSql VARCHAR(1000)
         ,@databaseName VARCHAR(100)
  -- If the backup directory does not end with ''\'', append one
  
  SET @databaseName = DB_NAME()

  IF CHARINDEX('\', REVERSE(@backupDirectory)) > 1
    SET @backupDirectory = @backupDirectory + '\'
  -- Create the backup file name based on the backup directory, the database name and today''s date
  SET @backupFileName = @backupDirectory + @databaseName + '-' + REPLACE(CONVERT(VARCHAR, GETDATE(), 110), '-', '.') + '.bak'
  
  BACKUP DATABASE @databaseName
  TO DISK = @backupFileName
  WITH NAME = @databaseName, RETAINDAYS = 15, NOFORMAT, INIT, SKIP, NOREWIND, NOUNLOAD, STATS = 1, CHECKSUM

  declare @backupSetId as int
select @backupSetId = position from msdb..backupset where database_name= @databaseName and backup_set_id=(select max(backup_set_id) from msdb..backupset where database_name=@databaseName )
if @backupSetId is null begin raiserror(N'Verify failed. Backup information for database  not found.', 16, 1) end
RESTORE VERIFYONLY FROM DISK = @backupFileName WITH  FILE = @backupSetId,  NOUNLOAD,  NOREWIND

GO

